import React, { useState, useEffect } from "react";
import axios from "axios";
import { Box, Button, Typography } from "@mui/material";

function App() {
  const [hardwareOption, setHardwareOption] = useState("Local Hardware");
  const [systemInfo, setSystemInfo] = useState({ cpu: "", total_ram: "", gpu: "" });

  useEffect(() => {
    // Fetch system info only when "Local Hardware" is selected
    if (hardwareOption === "Local Hardware") {
      fetchSystemInfo();
    }
  }, [hardwareOption]);

  const fetchSystemInfo = async () => {
    try {
      const response = await axios.get("http://localhost:5000/system-info");
      setSystemInfo(response.data);
    } catch (error) {
      console.error("Error fetching system info:", error);
      alert("Failed to fetch system information.");
    }
  };

  const handleHardwareClick = (option) => {
    setHardwareOption(option);
    if (option === "DSMLP") {
      setSystemInfo({ cpu: "", total_ram: "", gpu: "" }); // Clear system info if DSMLP is selected
    }
  };

  return (
    <Box display="flex" flexDirection="column" alignItems="center" padding="20px">
      {/* Title Section */}
      <Box
        display="flex"
        justifyContent="flex-start"
        alignItems="center"
        width="100%"
        marginBottom="20px"
      >
        <Typography variant="h6" style={{ fontWeight: "bold", fontSize: "14px" }}>
          CB-GUI
        </Typography>
      </Box>

      {/* Hardware Selection Section */}
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        width="40%"
        padding="20px"
        border="1px solid lightgray"
        borderRadius="8px"
        marginBottom="20px"
      >
        <Typography variant="h6" marginBottom="10px">
          Select Hardware
        </Typography>
        <Button
          variant={hardwareOption === "Local Hardware" ? "contained" : "outlined"}
          onClick={() => handleHardwareClick("Local Hardware")}
          style={{ marginBottom: "10px" }}
        >
          Local Hardware
        </Button>
        <Button
          variant={hardwareOption === "DSMLP" ? "contained" : "outlined"}
          onClick={() => handleHardwareClick("DSMLP")}
        >
          DSMLP
        </Button>

        {/* Display System Info for Local Hardware */}
        {hardwareOption === "Local Hardware" && systemInfo.cpu && (
          <Box marginTop="20px" textAlign="center">
            <Typography variant="subtitle1">
              <strong>CPU:</strong> {systemInfo.cpu}
            </Typography>
            <Typography variant="subtitle1">
              <strong>RAM:</strong> {systemInfo.total_ram}
            </Typography>
            <Typography variant="subtitle1">
              <strong>GPU:</strong> {systemInfo.gpu}
            </Typography>
          </Box>
        )}
      </Box>
    </Box>
  );
}

export default App;
