import React, { useState } from "react";
import axios from "axios";
import { Box, Button, Typography, TextField } from "@mui/material";

function App() {
  const [isTraining, setIsTraining] = useState(false);
  const [inputText, setInputText] = useState("");
  const [classificationResult, setClassificationResult] = useState("");
  const [activations, setActivations] = useState({});
  const [hardwareOption, setHardwareOption] = useState("Local Hardware"); // New state for hardware option

  const handleTrainClick = async () => {
    setIsTraining(true);
    try {
      const response = await axios.post("http://localhost:5000/train", {
        hardware: hardwareOption, // Send the selected hardware option to the backend
      });
      alert("Training completed: " + response.data.message);
    } catch (error) {
      console.error("Train Error:", error.response?.data || error.message);
      alert("Training failed. Check the backend logs.");
    } finally {
      setIsTraining(false);
    }
  };

  const handleClassifyClick = async () => {
    try {
      const response = await axios.post("http://localhost:5000/test", {
        input: inputText,
      });
      setClassificationResult(response.data.classification);
      setActivations(response.data.activations);
    } catch (error) {
      console.error("Classify Error:", error.response?.data || error.message);
      alert("Classification failed. Check the backend logs.");
    }
  };

  const handleHardwareClick = (option) => {
    setHardwareOption(option); // Update the selected hardware option
  };

  return (
    <Box
      display="flex"
      justifyContent="space-between"
      alignItems="center"
      height="100vh"
      padding="20px"
    >
      {/* Hardware Option Box */}
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        width="25%"
        padding="20px"
        border="1px solid lightgray"
        borderRadius="8px"
      >
        <Typography variant="h6" marginBottom="10px">
          Select Hardware
        </Typography>
        <Button
          variant={hardwareOption === "Local Hardware" ? "contained" : "outlined"}
          onClick={() => handleHardwareClick("Local Hardware")}
          style={{ marginBottom: "10px" }}
        >
          Local Hardware
        </Button>
        <Button
          variant={hardwareOption === "DSMLP" ? "contained" : "outlined"}
          onClick={() => handleHardwareClick("DSMLP")}
        >
          DSMLP
        </Button>
      </Box>

      {/* Train Model Box */}
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        width="25%"
        padding="20px"
        border="1px solid lightgray"
        borderRadius="8px"
      >
        <Typography variant="h6" marginBottom="10px">
          Train Model
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={handleTrainClick}
          disabled={isTraining}
        >
          {isTraining ? "Training..." : "Train"}
        </Button>
      </Box>

      {/* Classify Input Box */}
      <Box
        display="flex"
        flexDirection="column"
        justifyContent="center"
        alignItems="center"
        width="25%"
        padding="20px"
        border="1px solid lightgray"
        borderRadius="8px"
      >
        <Typography variant="h6" marginBottom="10px">
          Classify Input
        </Typography>
        <TextField
          label="Input Text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          fullWidth
          margin="normal"
        />
        <Button
          variant="contained"
          color="secondary"
          onClick={handleClassifyClick}
        >
          Classify
        </Button>
        {classificationResult && (
          <Typography marginTop="10px">
            Classification: {classificationResult}
          </Typography>
        )}
      </Box>
    </Box>
  );
}

export default App;
